import React, { useContext, useEffect, useState } from "react";
import { MovieContext } from "./MoviesContext";

function Watch() {
  const [search, setSearch] = useState("");
  const { watchList, handleRemoveFromWatchList, setWatchList } =
    useContext(MovieContext);
  const [genreList, setGenreList] = useState(["All Genre"]);
  const GENRE_NAME = {
    28: "Action",
    12: "Adventure",
    16: "Animation",
    35: "Comedy",
    80: "Crime",
    99: "Documentary",
    18: "Drama",
    10751: "Family",
    14: "Fantasy",
    36: "History",
    27: "Horror",
    10402: "Music",
    9648: "Mystery",
    10749: "Romance",
    878: "Sci-Fi",
    10770: "TV",
    53: "Thriller",
    10752: "War",
    37: "Western",
  };

  function handleSearch(e) {
    setSearch(e.target.value);
  }
  function handleIncrease() {
    let sortedList = watchList.sort(function (a, b) {
      return b.vote_average - a.vote_average;
    });

    setWatchList([...sortedList]);
  }

  function handleDecrease() {
    let sortedList = watchList.sort(function (a, b) {
      return b.vote_average - a.vote_average;
    });
    sortedList.reverse();

    setWatchList([...sortedList]);
  }
  useEffect(() => {
    let temp = watchList.map((movieObj) => GENRE_NAME[movieObj.genre_ids[0]]);
    temp = new Set(temp);
    setGenreList(["All Genre", ...temp]);
  }, [watchList]);
  return (
    <>
      <div className=" flex justify-center">
        {genreList.map((genre) => {
          return (
            <div
              className="h-[3rem] w-[15rem] bg-blue-400
                    rounded-xl text-white flex justify-center items-center 
                    font-bold "
            >
              {genre}
            </div>
          );
        })}
      </div>

      <div className="flex justify-center my-4">
        <input
          onChange={handleSearch}
          value={search}
          className="h-[3rem] w-[18rem] bg-gray-200
                 outline-none px-4 text-lg "
          placeholder="Search Movies"
          type="text"
        />
      </div>

      <div className="m-7 overflow-hidden rounded-lg border shadow-md	">
        <table className="p-4 w-full text-center">
          <thead className=" h-[3rem] bg-gray-50 border-b-2">
            <tr>
              <th>Title</th>
              <th className="flex">
                <div onClick={handleIncrease} className="p-2">
                  <i className="fa-solid fa-up-long"></i>
                </div>
                <div className="p-2"> Ratings</div>
                <div onClick={handleDecrease} className="p-2">
                  <i className="fa-solid fa-down-long"></i>
                </div>
              </th>
              <th>Popularity</th>
              <th>Genre</th>
              <th className=" text-red-500">Delete</th>
            </tr>
          </thead>
          <tbody>
            {watchList
              .filter((movieObj) => {
                return movieObj.title
                  .toLowerCase()
                  .includes(search.toLowerCase());
              })
              .map((movieObj) => {
                return (
                  <tr className=" border-b-2">
                    <td className="flex items-center mx-4 py-4">
                      {" "}
                      <img
                        className="h-[6rem] w-[10rem]"
                        src={
                          "https://image.tmdb.org/t/p/original/" +
                          movieObj.poster_path
                        }
                        alt=""
                      />
                      <div className="mx-4">{movieObj.title}</div>
                    </td>
                    <td>{movieObj.vote_average}</td>
                    <td>{movieObj.popularity}</td>
                    <td>{GENRE_NAME[movieObj.genre_ids[0]]}</td>
                    <td
                      onClick={() => handleRemoveFromWatchList(movieObj)}
                      className=" text-red-500 "
                    >
                      <i className="fa-solid fa-trash"></i>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Watch;
