import React, { useEffect, useState } from "react";
import Pagination from "./Pagination";
import MovieCard from "./MovieCard";
import axios from "axios";
export default function TrendingMovies() {
  const [movie, setMovies] = useState([]);
  useEffect(() => {
    let apiKey = "93adede0a1f1e4785f88203448555798";
    setIsLoading(true); // Set loading to true
    axios
      .get(`https://api.themoviedb.org/3/trending/movie/day?api_key=${apiKey}`)
      .then((response) => {
        setMovies(response.data.result);
      });
  }, []);

  if (movie.length === 0) {
    return <>...Loading</>;
  }

  return (
    <>
      <div className="text-center text-2xl font-bold text-stone-900 m-4	">
        Trending Movies
      </div>
      <div className="mx-2 flex flex-wrap justify-around gap-4 p-4	">
        <MovieCard title={movie[0].title} />
      </div>

      <Pagination />
    </>
  );
}

