import React from "react";

function MovieCard({ title, poster_path, movieObj }) {
  return (
    <>
      <div
        className=" flex hover:scale-[1.7] justify-center items-end h-80 w-56 rounded-lg bg-cover bg-no-repeat"
        style={{
          backgroundImage: `url(https://image.tmdb.org/t/p/original${poster_path})`,
        }}
      >
        <div className=" text-amber-700 py-0.5 bg-slate-950/50 w-full text-center	">
          {title}
        </div>
      </div>
    </>
  );
}

export default MovieCard;
